            });
          }

          if (!!errFn) {
            this.errFns.push(() => {
              try {
                const value = errFn(this.error);
                resolve(value);
              } catch (error) {
                reject(error);
              }
            });
          }
        }
      });
    }
    // 添加对Promise对象拒绝时的处理程序。
    catch(errFn) {
      return this.then(undefined, errFn);
    }
    // 添加对Promise对象解决或拒绝时的最终处理程序，无论Promise对象是否已被解决或拒绝。
    finally(fn) {
      setTimeout(() => {
        fn();
      }, 0);
    }
  }

  const isPromise = function(promise) {
    return (
      !!promise &&
      (typeof promise === "object" || typeof promise === "function") &&
      typeof promise.then === "function"
    );
  };

  /**
   * 接收一个可迭代对象（如数组），并返回一个新的Promise对象。
   * 当所有Promise对象都已解决时，该Promise对象才将被解决，并返回一个包含所有解决值的数组。
   * @param {any[]} iterable
   */
  MyPromise.all = function(iterable) {
    if (!(iterable instanceof Array)) {
      return console.log("传入参数必须是一个数组");
    }
    return new MyPromise((resolve, reject) => {
      let len = iterable.length;
      let count = 0;
      let results = new Array(len);
      for (let i = 0; i < len; i++) {
        let promise = iterable[i];
        count++;
        if (isPromise(promise)) {
          promise
            .then((res) => {
              results[i] = res;
              if (count === len) {
                resolve(results);
              }
            })
            .catch((err) => {
              reject(err);
            });
        } else if (typeof promise === "function") {
          results[i] = promise();
        } else {
          results[i] = promise;
        }
      }
      // 当数据的所有项都不是promise实例，我们就在这判断多一次，然后resolve
      if (count === len) {
        resolve(results);
      }
    });
  };

  MyPromise.race = function(iterable) {
    if (!(iterable instanceof Array)) {
      return console.log("传入参数必须是一个数组");
    }
    return new MyPromise((resolve, reject) => {
      iterable.forEach((p) => {
        if (isPromise(p)) {
          p.then((value) => {
            resolve(value);
          }).catch((err) => {
            reject(err);
          });
        } else if (typeof p === "function") {
          resolve(p());
        } else {
          resolve(p);
        }
      });
    });
  };
  // const p1 = new MyPromise((resolve, reject) => {
  //   console.log("状态pending");
  //   resolve("22222");
  //   reject("3333333");
  // });

  // p1.then((res) => {
  //   console.log("res1:", res);
  //   return "第二次的成功回调";
