   * @param {any[]} iterable
   */
  MyPromise.all = function(iterable) {
    if (!(iterable instanceof Array)) {
      return console.log("");
    }
    return new MyPromise((resolve, reject) => {
      let len = iterable.length;
      let count = 0;
      let results = new Array(len);
      for (let i = 0; i < len; i++) {
        let promise = iterable[i];
        count++;
        if (isPromise(promise)) {
          promise
            .then((res) => {
              results[i] = res;
              if (count === len) {
                resolve(results);
              }
            })
            .catch((err) => {
              reject(err);
            });
        } else if (typeof promise === "function") {
          results[i] = promise();
        } else {
          results[i] = promise;
        }
      }
      // promiseresolve
      if (count === len) {
        resolve(results);
      }
    });
  };

  MyPromise.race = function(iterable) {
    if (!(iterable instanceof Array)) {
      return console.log("");
    }
    return new MyPromise((resolve, reject) => {
      iterable.forEach((p) => {
        if (isPromise(p)) {
          p.then((value) => {
            resolve(value);
          }).catch((err) => {
            reject(err);
          });
        } else if (typeof p === "function") {
          resolve(p());
        } else {
          resolve(p);
        }
      });
    });
  };
